import React,{useState,useEffect} from 'react'
import { Icon } from 'react-icons-kit'

import {plus} from 'react-icons-kit/feather/plus'
import {edit2} from 'react-icons-kit/feather/edit2'
import {trash} from 'react-icons-kit/feather/trash'

const getTodosFromLS=()=>{
    const data = localStorage.getItem('Todos');
    if(data){
        return JSON.parse(data);
    }
    else{
        return []
    }
}

export const Form = () => {

    const [todoValue, setTodoValue]=useState('');

    const [todos, setTodos]=useState(getTodosFromLS());

    const handleSubmit=(e)=>{
        e.preventDefault();

        const date = new Date();
        const time = date.getTime();
        let todoObject={
          ID: time,
          TodoValue:todoValue,
          completed: false
        }
        setTodos([...todos,todoObject]);
        setTodoValue('');
    }

    // local storage
    useEffect(() => {
        localStorage.setItem('Todos',JSON.stringify(todos));
    }, [todos])

    // delete todo
    const handleDelete=(id)=>{
      const filtered = todos.filter((todo)=>{
        return todo.ID!==id
      });
      setTodos(filtered);
    }

    const [editForm, setEditForm]=useState(false);

    // id 
    const [id,setId]=useState();

    // edit todo
    const handleEdit=(todo,index)=>{
      setEditForm(true);
      setTodoValue(todo.TodoValue);
      setId(index);
    }

    // edit todo submit
    const handleEditSubmit=(e)=>{
      e.preventDefault();
      let items = [...todos];
      // storing the element at a particular index = id
      let item = items[id];
      // manipulating the item (object) keys
      item.TodoValue=todoValue;
      item.completed=false;
      // after manipulating item, saving it at the same index in items
      items[id]=item;
      // updating todos with items
      setTodos(items);
      setEditForm(false);
      setTodoValue('');
    }
   
    // handle checkbox
    const handleCheckbox=(id)=>{
      let todoArray=[];
      todos.forEach((todo)=>{
        if(todo.ID===id){
          if(todo.completed===false){
            todo.completed=true;
          }
          else if(todo.completed===true){
            todo.completed=false;
          }
        }
        todoArray.push(todo);
        setTodos(todoArray);
      })
    }

    return (
        <>

          {/* form component */}
          {editForm===false&&(
            <div className="form">
              <form autoComplete="off" onSubmit={handleSubmit}>
                <div className="input-and-button">
                  <input type='text' placeholder="Add an Item" required
                  onChange={(e)=>setTodoValue(e.target.value)} value={todoValue}/>
                  <div className='button'>
                    <button type="submit">
                      <Icon icon={plus} size={20}/>
                    </button>
                  </div>
                </div>
              </form>
            </div>
          )}

          {/* edit form component */}
          {editForm===true&&(
            <div className="form">
              <form autoComplete="off" onSubmit={handleEditSubmit}>
                <div className="input-and-button">
                  <input type='text' placeholder="Edit your Item" required
                  onChange={(e)=>setTodoValue(e.target.value)} value={todoValue}/>
                  <div className='button edit'>
                    <button type="submit">
                      UPDATE
                    </button>
                  </div>
                </div>
              </form>
            </div>
          )}

          {todos.length>0&&(
            <>
              {todos.slice(0).reverse().map((individualTodo,index)=>(
                <div className='todo' key={individualTodo.ID}>
                  <div>
                      {editForm===false&&(
                        <input type='checkbox' className='checkbox' id="checkbox" checked={individualTodo.completed}
                        onChange={()=>handleCheckbox(individualTodo.ID)}/>
                      )}
                      <label htmlFor="checkbox"></label>
                      <span
                      style={individualTodo.completed===true?{textDecoration:'line-through'}:{textDecoration:'none'}}>{individualTodo.TodoValue}</span>
                  </div>

                  {editForm===false&&(
                    <div className='edit-and-delete'>
                      <div style={{marginRight:7+'px'}}
                      onClick={()=>handleEdit(individualTodo,index)}>
                          <Icon icon={edit2} size={18}/>
                      </div>
                      <div onClick={()=>handleDelete(individualTodo.ID)}>
                          <Icon icon={trash} size={18}/>
                      </div>
                    </div>
                  )}

                </div>
              ))} 

              {/* delete all todos */}
              {editForm===false&&(
                <div style={{display:'flex',justifyContent:'flex-end'}}>
                  <button className='delete-all'
                  onClick={()=>setTodos([])}>Delete All Items</button>
                </div>
              )}
            </>
          )}

        </>
    )
}
